import React, { Suspense, memo } from 'react';
import type { FC, ReactNode } from 'react';
import { useRoutes, Link } from 'react-router-dom';
import { useSelector, shallowEqual } from 'react-redux';
import routes from '@/router';
import store from '@/store';

//[1] getState() 返回一个state对象，获得store中的所有state。而我们希望获得这个state对象的所有类型
const state = store.getState();
//[2]本质上，state对象的类型，就是 getState 这个函数的返回值类型。所以，问题变为获取 getState 的返回值类型
const IRootState = typeof state;

interface IProps {
  children?: ReactNode;
}

const Main: FC<IProps> = (): JSX.Element => {
  const { count, message } = useSelector(
    (state: any) => ({
      count: state.counter.count,
      message: state.counter.message
    }),
    shallowEqual
  );

  return (
    <div className="container">
      <div className="nav">
        <Link to="./discover">发现音乐</Link>
        <Link to="./mine">我的</Link>
      </div>
      <div>当前计数：{count}</div>
      <div>当前消息：{message}</div>
      <Suspense fallback={'loading component...'}>
        <div className="main">{useRoutes(routes)}</div>
      </Suspense>
    </div>
  );
};

export default memo(Main);
