import { createSlice } from '@reduxjs/toolkit';

const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    count: 100,
    name: 'ninjee',
    message: 'Hello Redux'
  },
  reducers: {
    changeMessage(state, { payload }) {
      state.message = payload;
    }
  }
});

export default counterSlice.reducer;
